import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { Link, useHistory } from 'react-router-dom';

const Signin = () => {
    const history = useHistory();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const changeOnClick = async (e) => {
        e.preventDefault();
        const users = {
            username,
            password,
        };

        try {
            const res = await axios.post('http://localhost:8080/user/signin', users);
            console.log('Server response:', res.data); // Log the server response for debugging

            if (res.data.success) {
                setMessage('Sign in successful!');
                history.push('/movie'); // Redirect to the movie page
            } else {
                setMessage(res.data.message || 'Sign in failed!');
            }
        } catch (err) {
            console.error('Error during sign-in:', err); // Log any error during sign-in
            if (err.response && err.response.data) {
                setMessage(err.response.data.message || 'Sign in failed!');
            } else {
                setMessage('Sign in failed! Please try again.');
            }
        }
    };

    return (
        <AddArticleContainer>
            <div className="container">
                <h1>Signin</h1>
                <span className="message">{message}</span>
                <form onSubmit={changeOnClick} encType="multipart/form-data">
                    <div className="form-group">
                        <label htmlFor="username">User Name</label>
                        <input
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            className="form-control"
                            placeholder="User Name"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="form-control"
                            placeholder="Password"
                        />
                    </div>
                    <button type="submit" className="btn btn-primary">Login</button>
                </form>
                <br />
                <Link to="/signup">
                    <h5>Don't have an account?</h5>
                </Link>
                <br />
                <Link to="/forgot">
                    <h5>Forgot Password</h5>
                </Link>
            </div>
        </AddArticleContainer>
    );
};

export default Signin;

const AddArticleContainer = styled.div`
    margin: 3rem auto;
    padding: 4rem;
    width: 31.25rem;
    h1 {
        font-weight: 900;
        color: var(--dark-green);
    }
    .btn-primary {
        margin-top: 2rem;
        background: var(--dark-green);
        border: none;
        &:hover {
            background: var(--light-green);
        }
    }
    .message {
        font-weight: 900;
        color: tomato;
        padding: 1rem 1rem 1rem 0;
    }
`;
